
$("body").on("click","#btn-add",function(a){a.preventDefault();showModal("form");$("#save_method").val("add");$("#id").val("");$("#name").val("");$("#rate").val("");$("#form-modal .md-input").each(function(){if($(this).val().length==0){$(this).parent("div.md-input-wrapper").removeClass("md-input-filled")}})});$("body").on("click",".btn-edit",function(a){a.preventDefault();$("#save_method").val("edit");var b=$(this).attr("data-id");$.ajax({url:current_url+"/get/"+b,method:"post",success:function(c){var d=JSON.parse(c);$("#id").val(d.id);$("#name").val(d.name);$("#rate").val(d.rate);$("#form-modal .md-input").each(function(){if($(this).val().length>0){$(this).parent("div.md-input-wrapper").addClass("md-input-filled")}});showModal("form")}})});